#!/bin/bash
# The Invariant - EC2 Initial Setup
# Run this on the EC2 instance after SSH'ing in

set -e

echo "ðŸš€ Setting up The Invariant backend server..."

# Update system
echo "ðŸ“¦ Updating system packages..."
sudo apt-get update
sudo apt-get upgrade -y

# Install Node.js 20.x
echo "ðŸ“¦ Installing Node.js 20.x..."
curl -fsSL https://deb.nodesource.com/setup_20.x | sudo -E bash -
sudo apt-get install -y nodejs
node --version
npm --version

# Install Postgres
echo "ðŸ“¦ Installing PostgreSQL..."
sudo apt-get install -y postgresql postgresql-contrib
sudo systemctl start postgresql
sudo systemctl enable postgresql

# Generate secure password
DB_PASSWORD=$(openssl rand -base64 32 | tr -d "=+/" | cut -c1-25)

# Create database and user
echo "ðŸ“¦ Creating database..."
sudo -u postgres psql <<EOF
CREATE DATABASE theinvariant;
CREATE USER theinvariant WITH PASSWORD '$DB_PASSWORD';
GRANT ALL PRIVILEGES ON DATABASE theinvariant TO theinvariant;
ALTER DATABASE theinvariant OWNER TO theinvariant;
\q
EOF

echo "âœ… Database created. Password saved to ~/.db_password"
echo "$DB_PASSWORD" > ~/.db_password
chmod 600 ~/.db_password

# Install Meilisearch
echo "ðŸ“¦ Installing Meilisearch..."
curl -L https://install.meilisearch.com | sh
sudo mv meilisearch /usr/local/bin/
sudo chmod +x /usr/local/bin/meilisearch

# Create Meilisearch systemd service
sudo tee /etc/systemd/system/meilisearch.service <<'EOF' > /dev/null
[Unit]
Description=Meilisearch
After=network.target

[Service]
Type=simple
User=ubuntu
ExecStart=/usr/local/bin/meilisearch --env production
Restart=always

[Install]
WantedBy=multi-user.target
EOF

sudo systemctl daemon-reload
sudo systemctl enable meilisearch
sudo systemctl start meilisearch

# Install Caddy
echo "ðŸ“¦ Installing Caddy..."
sudo apt-get install -y debian-keyring debian-archive-keyring apt-transport-https
curl -1sLf 'https://dl.cloudsmith.io/public/caddy/stable/gpg.key' | sudo gpg --dearmor -o /usr/share/keyrings/caddy-stable-archive-keyring.gpg
curl -1sLf 'https://dl.cloudsmith.io/public/caddy/stable/debian.deb.txt' | sudo tee /etc/apt/sources.list.d/caddy-stable.list
sudo apt-get update
sudo apt-get install -y caddy

# Install PM2 for process management
echo "ðŸ“¦ Installing PM2..."
sudo npm install -g pm2

# Install AWS CLI (for Parameter Store access)
echo "ðŸ“¦ Installing AWS CLI..."
sudo apt-get install -y awscli

# Create app directory
echo "ðŸ“¦ Creating app directories..."
sudo mkdir -p /var/www/theinvariant
sudo mkdir -p /var/www/theinvariant/backend
sudo mkdir -p /var/www/theinvariant/frontend
sudo chown -R ubuntu:ubuntu /var/www/theinvariant

# Create log directory
sudo mkdir -p /var/log/theinvariant
sudo chown ubuntu:ubuntu /var/log/theinvariant

# Create Caddy config
echo "ðŸ“¦ Configuring Caddy..."
sudo tee /etc/caddy/Caddyfile <<'CADDYFILE'
theinvariant.org {
    root * /var/www/theinvariant/frontend
    
    # Serve static assets first
    handle /_next/* {
        file_server
    }
    
    handle /favicon.* {
        file_server
    }
    
    # API proxy (includes /admin routes)
    handle /api/* {
        reverse_proxy localhost:3000
    }
    
    # Admin routes - SPA fallback
    handle /admin/* {
        try_files {path} /admin/index.html
        file_server
    }
    
    # SPA fallback for everything else
    handle {
        try_files {path} /index.html
        file_server
    }
}

api.theinvariant.org {
    reverse_proxy localhost:3000
}
CADDYFILE

# Enable and start Caddy
sudo systemctl enable caddy
sudo systemctl start caddy

# Install Python and pip (for future scripts)
sudo apt-get install -y python3 python3-pip

echo ""
